import React, { useEffect, useState } from "react";
import { Form, Button } from "react-bootstrap";
import axios from "axios";
import { toast } from "react-toastify";
function Login() {

 

  const [state , setState] = useState({
    name : "",
    email: ""
})



const handleChange = (e) => {
  const {id , value} = e.target   
  setState(prevState => ({
      ...prevState,
      [id] : value
  }))
}


const handleSubmitClick = (e) => {

  e.preventDefault();
axios
  .put("http://localhost:8080/users/newuser", {
    username: state.username,
    email: state.email,
  })
  .then(function (res) {
    toast.success("Logged In as "+ state.name)
      setState({
        name : "",
        email: ""
    })
  })
  .catch(function (e) {
    console.log(e)
  });
}
  console.log(state)


  return (
    <>
      <Form  >
        <Form.Group className="mb-3" controlId="name">
          <Form.Label>Enter Name</Form.Label>
          <Form.Control type="name" placeholder="Enter name" onChange={handleChange} />
          <Form.Text className="text-muted">
            We'll never share your email with anyone else.
          </Form.Text>
        </Form.Group>

        <Form.Group className="mb-3" controlId="email">
          <Form.Label>Enter Email</Form.Label>
          <Form.Control type="email" placeholder="Enter email" onChange={handleChange} />
        </Form.Group>
       
        <Button variant="success" type="submit" onClick={handleSubmitClick}>
          Submit
        </Button>
      </Form>
    </>
  );
}

export default Login;
